mapper.select:
SELECT o.id, o.aid, o.receiver, a.phone, o.address, o.paytype, o.total, o.pdate, o.ddate, o.edate, o.status FROM ordered o JOIN account a ON a.id = o.aid WHERE 1=1
AND o.id = #{id}
AND o.status != 'unpaid' ORDER BY o.id DESC

mapper.update:
UPDATE ordered
   SET status = #{status}
 WHERE id = #{id}

UPDATE order_detail
   SET status = #{status}
 WHERE id = #{id}

mapper.select:
SELECT * FROM (SELECT ROW_NUMBER() OVER (ORDER BY pdate DESC, o.id DESC) NUM , o.* , a.phone FROM ordered o JOIN account a ON a.id = o.aid WHERE 1=1
AND o.id = #{id}
AND o.ddate = #{ddate}
AND o.status != 'unpaid' ORDER BY pdate DESC, o.id DESC ) WHERE NUM BETWEEN (#{pageStart}+1) AND (#{pageStart} + #{perPageNum})

SELECT count(o.id) FROM ordered o WHERE o.id > 0
AND o.status = #{status}
AND o.status != 'unpaid'
