mapper.update:
UPDATE product
   SET title = #{title}
     , ptype = #{ptype}
     , price = #{price}
     , contents = #{contents}
     , active = #{active}
     , udate = SYSDATE
 WHERE id = #{id}

	UPDATE product_img
     SET name = #{imgname}
       , path = #{img}
       , url = #{url}
   WHERE pid = #{id}
