mapper.update:
UPDATE ordered
SET status = 'holding'
WHERE id = #{id}

UPDATE order_detail
SET status = 'holding'
WHERE oid = #{oid}

UPDATE ordered
SET status = 'delivered'
WHERE id = #{id}

UPDATE order_detail
SET status = 'delivered'
WHERE oid = #{oid}

UPDATE order_detail
SET status = 'paid'
WHERE oid = #{oid}

UPDATE ordered
SET status = 'paid',
	paytype = #{paytype}
WHERE id = #{id} AND aid=#{aid}
